package shane.leetcode.problems.easy;

import org.assertj.core.api.Assertions;
import org.junit.jupiter.api.Test;

public class Q389FindtheDifference {

    @Test
    void test() {
        Assertions.assertThat(findTheDifference("abcd", "abcde")).isEqualTo('e');
        Assertions.assertThat(findTheDifference("", "y")).isEqualTo('y');
        Assertions.assertThat(findTheDifference("aaa", "aaaa")).isEqualTo('a');
        Assertions.assertThat(findTheDifference(
                ""
                , "")).isEqualTo('t');
    }

    public char findTheDifference(String s, String t) {
        int[] arr = new int['z' - 'a' + 1];
        arr[t.charAt(0) - 'a']--;
        for (int i = 0; i < s.length(); i++) {
            arr[s.charAt(i) - 'a']++;
            arr[t.charAt(i + 1) - 'a']--;
        }
        for (int i = 0; i < arr.length; i++) {
            if (arr[i] < 0) {
                return (char) ('a' + i);
            }
        }
        return ' ';
    }

}
