    @Test
    public void test3() {
        assertThat(nearestExit(new char[][]{
                {'.', '+'}
        }, new int[]{0, 0})).isEqualTo(-1);
    }

    public int nearestExit(char[][] maze, int[] entrance) {
        Status status = new Status(entrance);
        Queue<Status> q = new LinkedList<>();
        q.offer(status);
        int steps = 1;
        while (!q.isEmpty()) {
            int size = q.size();
            for (int i = 0; i < size; i++) {
                Status poll = q.poll();
                List<Status> moves = poll.move(maze);
                for (Status move : moves) {
                    if (move.isExit)
                        return steps;
                }
                q.addAll(moves);
            }
            steps++;
        }
        return -1;
    }

    class Status {
        int y;
        int x;
        Set<String> history = new HashSet<>();
        boolean isExit = false;

        public Status(int[] entrance) {
            this.y = entrance[0];
            this.x = entrance[1];
            history.add(y + " " + x);
        }

        public Status(int nextY, int nextX, Set<String> history, int height, int width) {
            this.y = nextY;
            this.x = nextX;
            this.history = new HashSet<>(history);
            this.history.add(y + " " + x);

            if (x == 0 || x == width - 1 || y == 0 || y == height - 1) {
                this.isExit = true;
            }
        }

        public List<Status> move(char[][] maze) {
            int[][] moves = {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};
            List<Status> list = new ArrayList<>();
            int HEIGHT = maze.length;
            int WIDTH = maze[0].length;
            for (int[] move : moves) {
                int nextY = y + move[0];
                int nextX = x + move[1];
                if (0 <= nextX && nextX < WIDTH && 0 <= nextY && nextY < HEIGHT && maze[nextY][nextX] == '.' && !history.contains(nextY + " " + nextX)) {
                    Status next = new Status(nextY, nextX, history, HEIGHT, WIDTH);
                    list.add(next);
                }
            }
            return list;
        }

        @Override
        public String toString() {
            return "Status{" +
                    "y=" + y +
                    ", x=" + x +
                    ", history=" + history +
                    ", isExit=" + isExit +
                    '}';
        }
    }

