    public int minCost(int[] houses, int[][] cost, int m, int n, int target) {
        return bt(houses, cost, m, n, target, 0, 0);
    }

    private int bt(int[] houses, int[][] cost, int m, int n, int target, int price, int i) {
        if (i == m || target < 0) {
            if (i == m && target == 0)
                return price;
            return -1;
        }

        int min = -1;
        int currentColor = houses[i];
        int colorBefore = i > 0 ? houses[i - 1] : -1;
        if (currentColor == 0) {
            for (int j = 0; j < n; j++) {
                int c = cost[i][j];
                houses[i] = j + 1;
                min = min(min, bt(houses, cost, m, n, colorBefore == j + 1 ? target : target - 1, price + c, i + 1));
                houses[i] = currentColor;
            }
        } else {
            min = min(min, bt(houses, cost, m, n, colorBefore == currentColor ? target : target - 1, price, i + 1));
        }

        return min;
    }

    private int min(int a, int b) {
        if (a < 0) {
            return b;
        } else if (b < 0) {
            return a;
        } else {
            return Math.min(a, b);
        }
    }

