    int[][][] dp;

    public int minCost(int[] houses, int[][] cost, int m, int n, int target) {
        dp = new int[m + 1][n + 1][target + 1];
        return dfs(houses, cost, target, 0, 0);
    }

    int dfs(int[] houses, int[][] cost, int target, int i, int colorBefore) {
        if (i == houses.length || target < 0) {
            return target == 0 ? target : -1;
        }
        if (dp[i][colorBefore][target] != 0) {
            return dp[i][colorBefore][target];
        }

        if (houses[i] > 0) {
            return dfs(houses, cost, (colorBefore == houses[i] ? target : target - 1), i + 1, houses[i]);
        }

        int min = -1;
        for (int color = 1; color <= cost[0].length; color++) {
            int next = dfs(houses, cost, (colorBefore == color) ? target : target - 1, i + 1, color);
            if (next < 0)
                continue;
            min = min(min, cost[i][color - 1] + next);
        }

        dp[i][colorBefore][target] = min;
        return min;
    }

    private int min(int a, int b) {
        if (a < 0) {
            return b;
        } else if (b < 0) {
            return a;
        } else {
            return Math.min(a, b);
        }
    }

